import TextareaMarkdownEditor, { TextareaMarkdownEditorRef } from "textarea-markdown-editor";

import Advanced from "./examples/Advanced";
import QuickStart from "./examples/QuickStart";
import CustomComponent from "./examples/CustomComponent";
import CustomizeCommands from "./examples/CustomizeCommands";

function App() {
    return (
        <Fragment>
            <h2>QuickStart</h2>
            <QuickStart />
            <h2>CustomizeCommands</h2>
            <CustomizeCommands />
            <h2>CustomComponent</h2>
            <CustomComponent />
            <h2>Advanced</h2>
            <Advanced />
        </Fragment>
    );
}
export default App;
